@profiling_sect("time_math")
def lerp(a, b, n):
    return a*(1-n) + b*n

@profiling_sect("time_math")
def lerp2(a, b, n):
    t = 1 - (1 - n) * (1 - n)
    return a*(1-t) + b*t

